// src/MainComponents/Categories.jsx
import React, { useRef } from 'react';
import { Swiper, SwiperSlide } from 'swiper/react';
import 'swiper/css';
import 'swiper/css/navigation';
import 'swiper/css/pagination';
import { Navigation } from 'swiper/modules';
import { Pagination } from 'swiper/modules';

import CatA from '../CategoryComponets/CatA';
import Electronics from '../CategoryComponets/Electronics';
import Phone from '../CategoryComponets/Phone';
import Computer from '../CategoryComponets/Computer';
import Automobile from '../CategoryComponets/Automobile';
import Fashion from '../CategoryComponets/Fashion';
import Services from '../CategoryComponets/Services';
import Stationery from '../CategoryComponets/Stationery';
import Beauty from '../CategoryComponets/Beauty';
import Appliances from '../CategoryComponets/Appliances';
import Sports from '../CategoryComponets/Sports';

// List of categories with their components and a placeholder imageUrl
const categories = [
  { name: 'Electronics', component: Electronics, imageUrl: 'data:image/jpeg;base64,' },
  { name: 'Phones & Tablets', component: Phone, imageUrl: 'data:image/jpeg;base64,' },
  { name: 'Computers & Laptops', component: Computer, imageUrl: 'data:image/jpeg;base64,' },
  { name: 'Automobile', component: Automobile, imageUrl: 'data:image/jpeg;base64,' },
  { name: 'Fashion', component: Fashion, imageUrl: 'data:image/jpeg;base64,' },
  { name: 'Services', component: Services, imageUrl: 'data:image/jpeg;base64,' },
  { name: 'Stationery', component: Stationery, imageUrl: 'data:image/jpeg;base64,' },
  { name: 'Beauty & Health', component: Beauty, imageUrl: 'data:image/jpeg;base64,' },
  { name: 'Home Appliances', component: Appliances, imageUrl: 'data:image/jpeg;base64,' },
  { name: 'Sports & Outdoors', component: Sports, imageUrl: 'data:image/jpeg;base64,' },
];

function Categories() {
  const swiperRef = useRef(null);

  // Function to go to a specific category slide
  const goToCategory = (index) => {
    if (swiperRef.current) {
      swiperRef.current.slideTo(index);
    }
  };

  return (
    <div className="min-h-screen bg-gray-100 p-4">
      {/* Hero / Top Section */}
      <div className="mb-8">
        <CatA />
      </div>

      {/* Category Buttons Container - Centered and Scrollable */}
      <div className="flex justify-center mb-4">
        {/* Inner container to allow horizontal scrolling while main container is centered */}
        <div className="flex overflow-x-auto gap-4 py-2 px-2 scrollbar-hide"> 
          {categories.map((cat, index) => (
            <button
              key={index}
              onClick={() => goToCategory(index)}
              className="flex flex-col items-center justify-center p-2 rounded-lg bg-white border border-gray-200 hover:border-orange-600 transition duration-150 ease-in-out w-24 h-24 shrink-0 shadow-sm"
            >
              {/* Category Image */}
              <img 
                src={cat.imageUrl} 
                alt={cat.name} 
                className="w-10 h-10 object-cover rounded-full mb-1"
              />
              {/* Category Name */}
              <span className="text-xs font-medium text-gray-700 text-center truncate w-full">
                {cat.name}
              </span>
            </button>
          ))}
        </div>
      </div>
      
      {/* Swiper Slider */}
      <Swiper
        onSwiper={(swiper) => (swiperRef.current = swiper)}
        modules={[Navigation, Pagination]}
        navigation
        pagination={{ clickable: true }}
        spaceBetween={20}
        slidesPerView={1}
      >
        {categories.map((cat, index) => {
          const Component = cat.component;
          return (
            <SwiperSlide key={index}>
              <div className="bg-white rounded shadow-md p-4">
                <h2 className="text-2xl font-bold mb-4">{cat.name}</h2>
                {Component ? <Component /> : <p>No products available</p>}
              </div>
            </SwiperSlide>
          );
        })}
      </Swiper>
    </div>
  );
}

export default Categories;